import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

export default function Navbar() {
  const [isScrolled, setIsScrolled] = useState(false);

  // Add an event listener to handle scrolling
  useEffect(() => {
    const handleScroll = () => {
      // Check if the user has scrolled down more than 100 pixels (adjust as needed)
      if (window.scrollY > 100) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    // Attach the event listener
    window.addEventListener("scroll", handleScroll);

    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const navbarClasses = isScrolled
    ? "bg-opacity-90 backdrop-blur-md fixed top-0 left-0 right-0 z-10 transition-all duration-500"
    : "bg-opacity-0 backdrop-blur-0";

  return (
    <header className={navbarClasses}>
      <nav className="mx-auto flex max-w-7xl items-center justify-between p-3 lg:px-8" aria-label="Global">
        <div className="flex lg:flex-1">
          <a href="/" className="-m-1.5 p-1.5">
            <span className="sr-only">Remerge</span>
            <img className="h-8 w-auto" src="/images/logo.png" alt="" />
          </a>
        </div>
        <div className="flex lg:hidden">
          <button type="button" className="-m-2.5 inline-flex items-center justify-center rounded-md p-2.5 text-gray-700">
            <span className="sr-only">Open main menu</span>
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" aria-hidden="true">
              <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5" />
            </svg>
          </button>
        </div>
        <div className="hidden lg:flex lg:gap-x-12">
          <NavLink to="/dashboard" className="text-sm font-regular leading-6 text-gray-900" activeClassName="font-semibold">
            Product
          </NavLink>
          <NavLink to="/about" className="text-sm font-regular leading-6 text-gray-900" activeClassName="font-semibold">
            About
          </NavLink>
          <NavLink to="/pricing" className="text-sm font-regular leading-6 text-gray-900" activeClassName="font-semibold">
            Pricing
          </NavLink>
          <NavLink to="/contact" className="text-sm font-regular leading-6 text-gray-900" activeClassName="font-semibold">
            Contact
          </NavLink>
        </div>
        <div className="lg:flex lg:gap-x-12 inline-flex lg:flex-1 lg:justify-end items-center justify-center gap-x-6">
          <div className="hidden lg:flex lg:flex-1 lg:justify-end">
            <NavLink to="/login" className="text-sm font-semibold leading-6 text-gray-900" activeClassName="font-semibold">
              Log in <span aria-hidden="true">&rarr;</span>
            </NavLink>
          </div>
        </div>
      </nav>
    </header>
  );
}
